DROP TABLE IF EXISTS `timu`;
CREATE TABLE `timu` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `xinlicepingid` int(10) unsigned NOT NULL COMMENT '心理测评id',
  `cepingzhuti` varchar(255) NOT NULL COMMENT '测评主题',
  `bianhao` varchar(50) NOT NULL COMMENT '编号',
  `timu` text NOT NULL COMMENT '题目',
  `leixing` varchar(255) NOT NULL COMMENT '类型',
  `daan` text NOT NULL COMMENT '答案',
  `addtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '添加时间',
  PRIMARY KEY (`id`),
  KEY `timu_xinlicepingid_index` (`xinlicepingid`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8 COMMENT='题目';

